Module.prototype.load = function(filename) { debug('load %j for module %j', filename, this.id); // 保证模块没有加载过 assert(!this.loaded); this.filename = filename; this.paths = Module._nodeModulePaths(path.dirname(filename)); const extension = findLongestRegisteredExtension(filename); // allow .mjs to be overridden if (StringPrototypeEndsWith(filename, '.mjs') && !Module._extensions['.mjs']) throw new ERR_REQUIRE_ESM(filename, true); // 执行特定文件后缀名解析函数 如 js / json / node Module._extensions[extension](this, filename); this.loaded = true; const ESMLoader = asyncESM.ESMLoader; // Create module entry at load time to snapshot exports correctly const exports = this.exports; // Preemptively cache if ((module?.module === undefined || module.module.getStatus() < kEvaluated) && !ESMLoader.cjsCache.has(this)) ESMLoader.cjsCache.set(this, exports);
}; 下面是针对不同的文件后缀，加载 .js, .json, .node Module._extensions['.js'] = function(module, filename) { // If already analyzed the source, then it will be cached. const cached = cjsParseCache.get(module); let content; if (cached?.source) { content = cached.source; cached.source = undefined; } else { // 读取文件内容 content = fs.readFileSync(filename, 'utf8'); } if (StringPrototypeEndsWith(filename, '.js')) { const pkg = readPackageScope(filename); // Function require shouldn't be used in ES modules. if (pkg?.data?.type === 'module') { const parent = moduleParentCache.get(module); const parentPath = parent?.filename; const packageJsonPath = path.resolve(pkg.path, 'package.json'); const usesEsm = hasEsmSyntax(content); const err = new ERR_REQUIRE_ESM(filename, usesEsm, parentPath, packageJsonPath); // Attempt to reconstruct the parent require frame. if (Module._cache[parentPath]) { let parentSource; try { parentSource = fs.readFileSync(parentPath, 'utf8'); } catch {} if (parentSource) { const errLine = StringPrototypeSplit( StringPrototypeSlice(err.stack, StringPrototypeIndexOf( err.stack, ' at ')), '\n', 1)[0]; const { 1: line, 2: col } = RegExpPrototypeExec(/(\d+):(\d+)\)/, errLine) || []; if (line && col) { const srcLine = StringPrototypeSplit(parentSource, '\n')[line - 1]; const frame = `${parentPath}:${line}\n${srcLine}\n${ StringPrototypeRepeat(' ', col - 1)}^\n`; setArrowMessage(err, frame); } } } throw err; } } // 编译执行代码 module._compile(content, filename);
}; // Native extension for .json
Module._extensions['.json'] = function(module, filename) { // 直接按照 utf-8 格式加载文件 const content = fs.readFileSync(filename, 'utf8'); if (policy?.manifest) { const moduleURL = pathToFileURL(filename); policy.manifest.assertIntegrity(moduleURL, content); } try { // 以 JSON 对象格式导出文件内容 module.exports = JSONParse(stripBOM(content)); } catch (err) { err.message = filename + ': ' + err.message; throw err; }
}; // Native extension for .node
Module._extensions['.node'] = function(module, filename) { if (policy?.manifest) { const content = fs.readFileSync(filename); const moduleURL = pathToFileURL(filename); policy.manifest.assertIntegrity(moduleURL, content); } // Be aware this doesn't use `content` // 通过 process.dlopen 函数读取，而 process.dlopen 函数实际上调用了 C++ 代码中的 DLOpen 函数，而 DLOpen 中又调用了 uv_dlopen, 后者加载 .node 文件，类似 OS 加载系统类库文件 return process.dlopen(module, path.toNamespacedPath(filename));
}; Module.prototype._compile 编译 .js 文件 // Run the file contents in the correct scope or sandbox. Expose
// the correct helper variables (require, module, exports) to
// the file.
// Returns exception, if any.
Module.prototype._compile = function(content, filename) { let moduleURL; let redirects; if (policy?.manifest) { moduleURL = pathToFileURL(filename); redirects = policy.manifest.getDependencyMapper(moduleURL); policy.manifest.assertIntegrity(moduleURL, content); } maybeCacheSourceMap(filename, content, this); const compiledWrapper = wrapSafe(filename, content, this); let inspectorWrapper = null; if (getOptionValue('--inspect-brk') && process._eval == null) { if (!resolvedArgv) { // We enter the repl if we're not given a filename argument. if (process.argv[1]) { try { resolvedArgv = Module._resolveFilename(process.argv[1], null, false); } catch { // We only expect this codepath to be reached in the case of a // preloaded module (it will fail earlier with the main entry) assert(ArrayIsArray(getOptionValue('--require'))); } } else { resolvedArgv = 'repl'; } } // Set breakpoint on module start if (resolvedArgv && !hasPausedEntry && filename === resolvedArgv) { hasPausedEntry = true; inspectorWrapper = internalBinding('inspector').callAndPauseOnStart; } } const dirname = path.dirname(filename); const require = makeRequireFunction(this, redirects); let result; const exports = this.exports; const thisValue = exports; const module = this; if (requireDepth === 0) statCache = new SafeMap(); if (inspectorWrapper) { result = inspectorWrapper(compiledWrapper, thisValue, exports, require, module, filename, dirname); } else { result = ReflectApply(compiledWrapper, thisValue, [exports, require, module, filename, dirname]); } hasLoadedAnyUserCJSModule = true; if (requireDepth === 0) statCache = null; return result;
}; function wrapSafe(filename, content, cjsModuleInstance) { if (patched) { const wrapper = Module.wrap(content); return vm.runInThisContext(wrapper, { filename, lineOffset: 0, displayErrors: true, importModuleDynamically: async (specifier) => { const loader = asyncESM.ESMLoader; return loader.import(specifier, normalizeReferrerURL(filename)); }, }); } try { return vm.compileFunction(content, [ 'exports', 'require', 'module', '__filename', '__dirname', ], { filename, importModuleDynamically(specifier) { const loader = asyncESM.ESMLoader; return loader.import(specifier, normalizeReferrerURL(filename)); }, }); } catch (err) { if (process.mainModule === cjsModuleInstance) enrichCJSError(err, content); throw err; }
}

116.body:
es6 之后的模块化主要由 import 和 export 命令实现。export 用于从模块中导出实时绑定的函数、对象或原始值。import 则用于导入另一个模块导出的绑定。 // 以下两种为错误
// 因为 export 要导出的不是变量或者具体值，导出的是接口 export 1; const a = 1;
export a; // 以下为正确
// export 后面跟声明和赋值语句，就是导出接口
// export 后面跟花括号，花括号不是对象的意思，而是接口列表，里面放逗号分开的变量名 const a = 1;
export { a }; export const a = 1, b = 2; export const a = 1;
export const b = 2; const a = 1;
export { a as outA }; const a = 1;
const b = 2;
export { a as outA, b as outB }; // export default 后面是一个值，和 export 不一样， export 后面是一个接口
// export default 后面的花括号就是对象的意思了，不是接口列表 const a = 1;
export default a; const a = 1;
export default { a }; export default function() {};
export default class(){}; const a = 1;
export defalut a;
// 等价于
export { a as default } // import 导入的时候，如果对应的是 export 导出的接口，则 import 的时候也要导入接口，放在花括号里
// 如果对应的是 export default 导出的值，则不用放在花括号里 // 某个模块的导出 moudule.js
export const a = 1; import { a } from './module' import { a as myA } from './module' // 若是只想要运行被加载的模块可以这样写，但是即使加载 2 次也只是运行一次
import './module' // 整体加载
import * as module from './module' // default 接口和具名接口
import module, { a } from './module'

