class Subject { observers = [] addObserver(observer) { this.observers.push(observer) } removeObserver(observer) { let index = this.observers.indexOf(observer) if (index > -1) { this.observers.splice(index, 1) } } notify() { this.observers.forEach(observer => { observer.update() }) }
} class Observer{ constructor(fn) { this.update = fn; } subscribeTo(subject) { subject.addObserver(this) }
} // 测试代码 let subject = new Subject()
let observer = new Observer(function() { console.log('update 调用了')
})
observer.subscribeTo(subject) subject.notify()

