class EventBus { map = {} on(type, handler) { this.map[type] = (this.map[type] || []).concat(handler) } once(type, handler) { const _this = this function cb() { _this.off(type, cb) handler.apply(_this, arguments); } this.on(type, cb) } fire(type, data) { this.map[type] && this.map[type].forEach(handler => handler(data)) } off(type, handler) { if(this.map[type]) { if(!handler) { delete this.map[type] } else { let index = this.map[type].indexOf(handler) if (index >= 0) { this.map[type].splice(index, 1) } } } }
