def(arrayMethods, method, function mutator (...args) { // 先执行原生方法拿到结果，比如 push.apply(this, args) const result = original.apply(this, args) const ob = this.__ob__ let inserted // 如果 method 是以下三个之一 push unshift splice，说明是新插入了元素 switch (method) { case 'push': case 'unshift': inserted = args break case 'splice': inserted = args.slice(2) break } // 如果新增了元素，对新插入的元素做响应式处理 if (inserted) ob.observeArray(inserted) // notify change ob.dep.notify() // 手动触发更新 return result
