) { with(this){return _c('div',{attrs:{"id":"demo"}},[_c('p',[_v(_s(obj.foo))]),_v(" "),_l((arr),function(a){return _c('div',{key:a},[_v("\n "+_s(a)+"\n ")])})],2)}
