import { isArray } from '@vue/shared';
import { createDep, Dep } from './dep'; type KeyToDepMap = Map<any, Dep>;
/** * 收集所有依赖的 WeakMap 实例： * 1. `key`：响应性对象 * 2. `value`：`Map` 对象 * 1. `key`：响应性对象的指定属性 * 2. `value`：指定对象的指定属性的执行函数生成的 ReactiveEffect 实例组成的 Set 数组 */
const targetMap = new WeakMap<any, KeyToDepMap>(); export function effect<T = any>(fn: () => T) { const _effect = new ReactiveEffect(fn); _effect.run();
} export let activeEffect: ReactiveEffect | undefined; export class ReactiveEffect<T = any> { constructor(public fn: () => T) {} run() { activeEffect = this; return this.fn(); }
} /** * 用于收集依赖的方法 * @param target WeakMap 的 key * @param key 代理对象的 key，当依赖被触发时，需要根据该 key 获取 */
export function track(target: object, key: unknown) { // 如果当前不存在执行函数，则直接 return if (!activeEffect) return; // 尝试从 targetMap 中，根据 target 获取 map let depsMap = targetMap.get(target); // 如果获取到的 map 不存在，则生成新的 map 对象，并把该对象赋值给对应的 value if (!depsMap) { targetMap.set(target, (depsMap = new Map())); } // 获取指定 key 的 dep let dep = depsMap.get(key); // 如果 dep 不存在，则生成一个新的 dep，并放入到 depsMap 中 if (!dep) { { depsMap.set(key, (dep = createDep())); } } trackEffects(dep);
} /** * 利用 dep 依次跟踪指定 key 的所有 effect * @param dep */
export function trackEffects(dep: Dep) { // activeEffect! ： 断言 activeEffect 不为 null dep.add(activeEffect!);
} /** * 触发依赖的方法 * @param target WeakMap 的 key * @param key 代理对象的 key，当依赖被触发时，需要根据该 key 获取 */
export function trigger(target: object, key: unknown, newValue: unknown) { // 依据 target 获取存储的 map 实例 const depsMap = targetMap.get(target); // 如果 map 不存在，则直接 return if (!depsMap) { return; } // 依据指定的 key，获取 dep 实例 const dep = depsMap.get(key) as Dep; // dep 不存在则直接 return if (!dep) { return; } // 触发 dep trackEffects(dep);
} /** * 依次触发 dep 中保存的依赖 */
export function triggerEffects(dep: Dep) { // 把 dep 构建为一个数组 const effects = isArray(dep) ? dep : [...dep]; // 依次触发 for (const effect of effects) { triggerEffect(effect); }
} /** * 触发指定的依赖 */
export function triggerEffect(effect: ReactiveEffect) { effect.fn();
}

