computed 调用后，我们传递给 computed 的函数被设置为 getter，setter 没有设置是空函数，然后创建 ComputedRefImpl 实例 export function computed<T>( getterOrOptions: ComputedGetter<T> | WritableComputedOptions<T>, debugOptions?: DebuggerOptions, isSSR = false
) { let getter: ComputedGetter<T>; let setter: ComputedSetter<T>; const onlyGetter = isFunction(getterOrOptions); if (onlyGetter) { // 传递给 computed 的函数被设置为 getter getter = getterOrOptions; // setter 没有设置是空函数 setter = __DEV__ ? () => { console.warn('Write operation failed: computed value is readonly'); } : NOOP; } else { getter = getterOrOptions.get; setter = getterOrOptions.set; } // 创建 ComputedRefImpl 实例 const cRef = new ComputedRefImpl( getter, setter, onlyGetter || !setter, isSSR ); if (__DEV__ && debugOptions && !isSSR) { cRef.effect.onTrack = debugOptions.onTrack; cRef.effect.onTrigger = debugOptions.onTrigger; } return cRef as any;
} 创建 ComputedRefImpl 实例 export class ComputedRefImpl<T> { // dep属性 是一个 set 数组，里面存放的都是收集到的 reactiveEffect 依赖，和之前的 refImpl 类一样 public dep?: Dep = undefined; private _value!: T; public readonly effect: ReactiveEffect<T>; // ref 标识符为 true public readonly __v_isRef = true; public readonly [ReactiveFlags.IS_READONLY]: boolean; // 脏变量为 true（有重要作用） public _dirty = true; public _cacheable: boolean; constructor( getter: ComputedGetter<T>, private readonly _setter: ComputedSetter<T>, isReadonly: boolean, isSSR: boolean ) { // 新建了一个 ReactiveEffect，第一个参数 getter 就是传递给 computed 的函数，也就是说传递给 computed 的函数和使用 effect api 时候传递给 effect 的函数是一样的效果，就是依赖触发时的 fn this.effect = new ReactiveEffect(getter, () => { // 判断脏变量如果不是 true，就让脏变量变成 true，然后触发依赖 // 因此可以得知 _dirty 脏变量用来控制什么时候触发依赖 if (!this._dirty) { this._dirty = true; triggerRefValue(this); } }); this.effect.computed = this; this.effect.active = this._cacheable = !isSSR; this[ReactiveFlags.IS_READONLY] = isReadonly; } get value() { // the computed ref may get wrapped by other proxies e.g. readonly() #3376 const self = toRaw(this); trackRefValue(self); if (self._dirty || !self._cacheable) { self._dirty = false; self._value = self.effect.run()!; } return self._value; } set value(newValue: T) { this._setter(newValue); }
} 接着走到 effect(() => {document.querySelector('#app').innerText = computedObj.value;});， 会触发 ComputedRefImpl 的 getter export class ComputedRefImpl<T> { // dep属性 是一个 set 数组，里面存放的都是收集到的 reactiveEffect 依赖，和之前的 refImpl 类一样 public dep?: Dep = undefined; private _value!: T; public readonly effect: ReactiveEffect<T>; // ref 标识符为 true public readonly __v_isRef = true; public readonly [ReactiveFlags.IS_READONLY]: boolean; // 脏变量为 true（有重要作用） public _dirty = true; public _cacheable: boolean; constructor( getter: ComputedGetter<T>, private readonly _setter: ComputedSetter<T>, isReadonly: boolean, isSSR: boolean ) { // 新建了一个 ReactiveEffect，第一个参数 getter 就是传递给 computed 的函数，也就是说传递给 computed 的函数和使用 effect api 时候传递给 effect 的函数是一样的效果，就是依赖触发时的 fn this.effect = new ReactiveEffect(getter, () => { // 判断脏变量如果不是 true，就让脏变量变成 true，然后触发依赖 // 因此可以得知 _dirty 脏变量用来控制什么时候触发依赖 if (!this._dirty) { this._dirty = true; triggerRefValue(this); } }); this.effect.computed = this; this.effect.active = this._cacheable = !isSSR; this[ReactiveFlags.IS_READONLY] = isReadonly; } get value() { // the computed ref may get wrapped by other proxies e.g. readonly() #3376 const self = toRaw(this); // trackRefValue 收集依赖，使当前 effect 的匿名函数对应的 reactiveEffect 与当前 ComputedRefImpl 实例建立联系，类似 ref api 的逻辑 trackRefValue(self); // getter 触发的时候，如果脏状态为 true，说明是依赖收集阶段，这里已经收集完成，将脏状态变为 false if (self._dirty || !self._cacheable) { self._dirty = false; // 执行传递给 computed 的函数，第一次获取到计算属性的值存到 _value 里 // 传递给 computed 的函数是 () => {return '姓名： ' + obj.name;}，执行的时候又会触发 reactive 对象 obj 的 getter，然后进行 obj 这个 reactive 对象的依赖收集 self._value = self.effect.run()!; } return self._value; } set value(newValue: T) { this._setter(newValue); }
}

