});
delete obj.p
// TypeError: Cannot delete property 'p' of #<Object> 只设置了取值器的属性不可写 'use strict'; var obj = { get v() { return 1; } }; obj.v = 2; // Uncaught TypeError: Cannot set property v of #<Object> which has only a getter 禁止扩展的对象不可扩展 'use strict'; var obj = {}; Object.preventExtensions(obj); obj.v = 1; // Uncaught TypeError: Cannot add property v, object is not extensible eval、arguments 不可用作标识名 'use strict'; var eval = 17; var arguments = 17; var obj = { set p(arguments) { } }; try { } catch (arguments) { } function x(eval) { } function arguments() { } var y = function eval() { }; var f = new Function('arguments', "'use strict'; return 17;"); // SyntaxError: Unexpected eval or arguments in strict mode 函数不能有重名的参数 function f(a, a, b) { 'use strict'; return a + b; } // Uncaught SyntaxError: Duplicate parameter name not allowed in this context 禁止八进制的前缀0表示法 'use strict'; var n = 0100; // Uncaught SyntaxError: Octal literals are not allowed in strict mode. 增强的安全措施 全局变量显式声明 'use strict'; v = 1; // 报错，v未声明 for (i = 0; i < 2; i++) { // 报错，i 未声明 // ... } function f() { x = 123; } f() // 报错，未声明就创建一个全局变量 禁止 this 关键字指向全局对象 // 正常模式 function f() { console.log(this === window); } f() // true // 严格模式 function f() { 'use strict'; console.log(this === undefined); } f() // true 禁止使用 fn.callee、fn.caller function f1() { 'use strict'; f1.caller; // 报错 f1.arguments; // 报错 } f1(); 禁止使用 arguments.callee、arguments.caller 'use strict'; var f = function () { return arguments.callee; }; f(); // 报错 禁止删除变量 'use strict'; var x; delete x; // 语法错误 var obj = Object.create(null, { x: { value: 1, configurable: true } }); delete obj.x; // 删除成功 静态绑定 JavaScript 语言的一个特点，就是允许“动态绑定”，即某些属性和方法到底属于哪一个对象，不是在编译时确定的，而是在运行时（runtime）确定的。严格模式对动态绑定做了一些限制。某些情况下，只允许静态绑定。也就是说，属性和方法到底归属哪个对象，必须在编译阶段就确定。这样做有利于编译效率的提高，也使得代码更容易阅读，更少出现意外。 禁止使用 with 语句 'use strict'; var v = 1; var obj = {}; with (obj) { v = 2; } // Uncaught SyntaxError: Strict mode code may not include a with statement 创设 eval 作用域 正常模式下，JavaScript 语言有两种变量作用域（scope）：全局作用域和